import { computed, ref } from "vue";
import { useStore } from "vuex";
import { useRoute } from "vue-router";
export default {
  name: "SingleProduct",

  setup() {
    const route = useRoute();
    const {
      id
    } = route.params;
    const store = useStore();
    store.dispatch("getProductById", id);
    return {
      product: computed(() => store.getters.product)
    };
  }

};
