import axios from "@/axios";

export default {
    state: {
        user: null,
        loggedIn: false,
    },
    getters: {
        loggedIn: (state) => loggedIn,
    },
    mutations: {
        SET_USER: (state, user) => {
            state.user = user;
            state.isLoggedIn = true;
            console.log(user);
        },
    },
    actions: {
        login: async({ commit }, user) => {
            const res = await axios.post("users/login", user);
            if (res.status === 200) {
                commit("SET_USER", res.data);
            }
        },
    },
};
