export default {
    state: {
        cart: [],
    },
    getters: {
        cart: (state) => state.cart,
    },
    mutations: {
        ADD_ITEM: (state, newItem) => {
            state.cart.push(newItem);
        },
    },
    actions: {
        addItem: async({ commit }, id, amount) => {
            const res = await axios.get("/api/products/" + id);
            const product = res.data;

            const newItem = {
                title: product.name,
                img: product.imgURL,
                price: product.price,
                amount,
            };

            commit("ADD_ITEM", newItem);
        },
    },
};
