import axios from "axios";

export default {
    state: {
        user: null,
        loggedIn: false,
    },
    getters: {
        loggedIn: (state) => loggedIn,
    },
    mutations: {},
    actions: {
        login: async({ commit }, user) => {
            const res = await axios.post("/api/users/login", user);
            if (res.status === 200) {
                commit("SET_USER", res.data);
            }
        },
    },
};
