import axios from "axios";

export default {
    state: {
        cart: [],
    },
    getters: {
        cart: (state) => state.cart,
        cartItemAmount: (state) => (id) =>
            state.cart.find((item) => item.id === id).amount,
    },
    mutations: {
        ADD_ITEM: (state, { product, id, amount }) => {
            const itemExists = state.cart.find((item) => item.id === id);
            if (itemExists) {
                itemExists.amount += amount;
            } else {
                const newItem = {
                    id,
                    title: product.name,
                    img: product.imgURL,
                    price: product.price,
                    amount,
                };

                state.cart.push(newItem);
            }

            //console.log(state.cart);
        },

        UPDATE_AMOUNT: (state, { id, amount }) => {
            const itemExists = state.cart.find((item) => item.id === id);
            if (itemExists) {
                if (amount === "" || amount <= 1) {
                    itemExists.amount = 1;
                } else {
                    itemExists.amount = amount;
                }

                //console.log(state.cart);
            }
        },
    },
    actions: {
        addItem: async({ commit }, { id, amount }) => {
            const res = await axios.get("/api/products/" + id);

            commit("ADD_ITEM", { product: res.data, id, amount });
        },

        updateAmount: async({ commit }, item) => {
            commit("UPDATE_AMOUNT", item);
        },

        removeItem: async({ commit }, item) => {
            commit("REMOVE_ITEM", item);
        },
    },
};
